// Array useful for letter-to-digit number conversion
numArray = new Array ("", "uno", "due", "tre", "quattro", "cinque", "sei", "sette", "otto", "nove");
// Regular expression useful for extracting only the part containing the lettered number
let reg = new RegExp('^([a-z])+');
let tags = [];

try {
    // Buffer to String
    msg.payload = msg.payload.toString('ascii');
    if (msg.payload === "Collegamento con MQTT" || msg.payload === "reset") {
        return;
    }

    let parsePayload = JSON.parse(msg.payload);

    parsePayload.Reader0.tag = parsePayload.Reader0.tag.slice(0, -2);
    parsePayload.Reader1.tag = parsePayload.Reader1.tag;
    parsePayload.Reader2.tag = parsePayload.Reader2.tag.slice(0, -2);
    parsePayload.Reader3.tag = parsePayload.Reader3.tag.slice(0, -2);
    parsePayload.Reader4.tag = parsePayload.Reader4.tag.slice(0, -2);

    // If one of the 5 tags is empty, skip this function
    if (parsePayload.Reader0.tag === "") return;
    if (parsePayload.Reader1.tag === "") return;
    if (parsePayload.Reader2.tag === "") return;
    if (parsePayload.Reader3.tag === "") return;
    if (parsePayload.Reader4.tag === "") return;

    // Save the various components of the expression in the array
    tags[0] = numArray.indexOf(parsePayload.Reader0.tag.match(reg)[0]); // first number
    tags[1] = parsePayload.Reader1.tag.match(reg)[0];                   // sign operation type
    tags[2] = numArray.indexOf(parsePayload.Reader2.tag.match(reg)[0]); // second number
    tags[3] = parsePayload.Reader3.tag.match(reg)[0];                   // sign comparison/comparison
    tags[4] = numArray.indexOf(parsePayload.Reader4.tag.match(reg)[0]); // result

    msg.payload = {
        'tags': tags
    };
} catch (error) {
    msg.payload = {
        'Err': error.stack
    }
}

return msg;

let math_results = {
    "result": null,
    "isCorrect": null
}
try {
    let num1 = msg.payload.tags[0];
    let oper1 = msg.payload.tags[1];
    let num2 = msg.payload.tags[2];
    let oper2 = msg.payload.tags[3];
    let num3 = msg.payload.tags[4];
    let result;
    let isCorrect;

    if ((num1 === "" || isNaN(num1)) || (num2 === "" || isNaN(num2)) || num3 === "" || isNaN(num3))
        throw "Not a valid math expression!";
    // If to convert from letters the type of operation
    if (oper1 == "+") {
        result = num1 + num2;
    } else if (oper1 == "-") {
        result = num1 - num2;
    } else if (oper1 == "x") {
        result = num1 * num2;
    } else if (oper1 == "/") {
        result = num1 / num2;
    } else {
        throw "Not a valid math expression!";
    }

    // If to convert from letters to comparison/comparison sign
    if (oper2 == "=") {
        isCorrect = result === num3;
    } else if (oper2 == ">") {
        isCorrect = result > num3;
    } else if (oper2 == "<") {
        isCorrect = result < num3;
    } else {
        throw "Not a valid math expression!";
    }
    math_results["result"] = result
    math_results["isCorrect"] = isCorrect
} catch (error) {
    //msg.payload = "\n"+ error
    //node.warn ("ResultGame function error\n" + error)
} finally {
    // Query to retrieve rules

    // I set the tags as a global variable
    global.set("tags",msg.payload["tags"]);

    msg = {
        "board_result" : math_results,
        "event" : msg.event
    };
    return msg;
}

try {
    let validRules = msg.payload.validRules;
    if (validRules.validRules.length > 0) {
        let url = "";
        let color = "";

        // Word to RGB color
        var colors = {
            "rosso" : "255,0,0",
            "verde" : "0,255,0",
            "blu"   : "0,0,255",
            "nulla" : "256,0,0",
            "spegni": "0,0,0"
        };

        var audios = {
            "success" : "vittoria",
            "fail" : "sconfitta"
        }
        let color_changed = false
        for(var i=0; i<validRules["actions"].length; i++) {
            if(validRules["actions"][i].startsWith("A")) 
            { // "A-nomeFileaudio"
                url = validRules["actions"][i].substr(2);
                url = audios[url]
            } 
            else 
            { // "C-colore"
                color = validRules["actions"][i].substr(2);
                //node.warn ("Color is: " + color)
                if (color == 'blue') {
                    color = 'blu'
                    color_changed = color
                } else if (color == 'green') {
                    color = 'verde'
                    color_changed = color

                } else if (color == 'red') {
                    color = 'rosso'
                    color_changed = color

                } else if (color == 'off'){
                    color = 'spegni'
                    color_changed = color
                }
            }
        }
        /*
        if (typeof colors[color.toLowerCase()] != 'undefined') {
            color = colors[color.toLowerCase()];
        }*/

        if (color_changed) { //If the last rule was "nulla", the color would never change otherwise 
            color = colors[color_changed] 
        } else {
            color = colors["nulla"] 
        }

        msg.payload = {
            "URL": url,
            "LED": color
        }

        return msg;
    }

} catch (error) {
    return;
}

let validRules = [];
let isValid;
var count = Object.keys(msg.payload["Rules"]).length;
let actions = [];

for(var i=0; i<count; i++) {
    // Only enabled rules are considered
    if(msg.payload["Rules"]["Rule"+i]["enabled"] == 1) {
        isValid = true;

        //Event
        let eventJSON = msg.payload["Rules"]["Rule"+i]["event_data"];
        //Check if the actual event matches with the event defined in the rule 

        //node.warn ("Msg.event: "+msg.payload.event)
        // node.warn ("Rule event: "+eventJSON["evt"])
        if (msg.payload.event !== eventJSON["evt"]) {
            let expr_correct = msg.payload["board_result"].isCorrect
            if (expr_correct !== null) 
            {
                if (eventJSON["evt"] === 'expr_correct') {
                    if (!expr_correct)
                        isValid = false;
                } 
                else if (eventJSON["evt"] === 'expr_wrong') {
                    if (expr_correct)
                        isValid = false;
                }
                else { // Error 
                    isValid = false;
                }
            }
            else { // Error 
                isValid = false;
            }
        }
        //if (isValid) node.warn ("Event "+ i + " is valid! " )


        if (! isValid) continue;

        //States
        let stateJSON = msg.payload["Rules"]["Rule"+i]["state_data"];

        for(var j=0; j<stateJSON.length; j++) {
            let card_state = msg.payload["Rules"]["Rule"+i]["state_data"][j]["card"];
            let pos_state = msg.payload["Rules"]["Rule"+i]["state_data"][j]["position"]-1;

            //State = "Tile in position pos_state is equal to card_state"
            if(msg.payload["tags"][pos_state] != card_state) {
                isValid = false; //Solo se è uguale
                //node.warn ("state mismatch:")
                //node.warn ("In position "+pos_state+": ")
                //node.warn ("Expected: "+msg.payload["tags"][pos_state] + ", received: " +card_state)
                continue;
            }
        }

        // If the rule is valid, then actions are saved
        if(isValid) {
            //node.warn ("Rule "+ i + " is valid! " )
            //Action
            let actionJSON = msg.payload["Rules"]["Rule"+i]["action_data"];

            for(j=0; j<actionJSON.length; j++) {
                if(actionJSON[j].hasOwnProperty("audio")) {
                    actions.push("A-" + actionJSON[j]["audio"]);
                } else {
                    actions.push("C-" + actionJSON[j]["colour"]);
                }
            }

            validRules.push(i);
        }
    }
}

msg.payload["validRules"] = {
    "validRules" : validRules,
    "actions" : actions
};
return msg;

