// Array useful for letter-to-digit number conversion
let numArray = new Array ("zero", "uno", "due", "tre", "quattro", "cinque", "sei", "sette", "otto", "nove");
// Regular expression useful for extracting only the part containing the lettered number
let reg = new RegExp('^([a-z])+');
let tags = [];
let symbols = {
    "uguale" : "=",
    "minore" : "<",
    "maggiore" : ">",
    "addizione" : "+",
    "sottrazione" : "-",
    "moltiplicazione" : "x",
    "moltiplicazion" : "x",
    "divisione" : "/"
}

let parseVal = function (str) {
    if (str === "") {
        return ""
    }
    let temp = str.match(reg)[0]
    let val = numArray.indexOf(temp); 
    if (val === -1) { // if not a number
        val = symbols [temp]
        if (! val) {
            return str
        }
    }
    return val
}

let parseValSpec = function (str) {
    if (str === "") {
        return ""
    }
    let temp = str.match(reg)[0]
    let val = numArray.indexOf(temp); 
    if (val === -1) { // if not a number
        val = symbols [temp]
        if (! val) {
            return str
        }
    } else if (val === 9) {
        return "="
    }
    return val
}

