try {
    // A new JSON is created to overwrite the original payload
    newPayload = {};
    newPayload["Rules"] = {};

    // The structure you want to create is Rules.Rules0
    for(var i=0; i<msg.payload.length; i++) {
        newPayload["Rules"]["Rule"+i] = msg.payload[i]
    }

    // I also save tags in msg.payload.tags
    newPayload["tags"] = global.get("tags");

    for (var k=0; k<msg.payload.length; k++) {
        // The parse of the three JSON files is performed
        newPayload["Rules"]["Rule"+k]["action_data"] = JSON.parse(newPayload["Rules"]["Rule"+k]["action_data"]);
        newPayload["Rules"]["Rule"+k]["event_data"] = JSON.parse(newPayload["Rules"]["Rule"+k]["event_data"]);
        newPayload["Rules"]["Rule"+k]["state_data"] = JSON.parse(newPayload["Rules"]["Rule"+k]["state_data"]);

        action = newPayload["Rules"]["Rule"+k]["action_data"];
        state = newPayload["Rules"]["Rule"+k]["state_data"];

        /* ACTIONS */
        // Audio or colour parameters are extracted from the action label
        for (i=0; i<action.length; i++) {
            let act = newPayload["Rules"]["Rule"+k]["action_data"][i].label;
            if (act) {
                if (act.match('Play')) { //Riproduci audio //act.substr(0, 1) === 'R'
                //let reg = /audio(?:(?!^\.)[\s\S])+\.mp3/g;
                //let audio = act.match(reg);
                let audio = action[i]["parameters"][0]["value"]
                newPayload["Rules"]["Rule"+k]["action_data"][i]["audio"] = audio.toString(); // = audio.toString().substr(6);

                } else if (act.match('Turn')) { //Accendi led 
                    //let reg = /,(?:(?!^\.)[\s\S])+\s/g
                    //let colour = act.match(reg);
                    if (act.match('on')) {
                        let colour = action[i]["parameters"][0]["value"]
                        newPayload["Rules"]["Rule"+k]["action_data"][i]["colour"] = colour.toString() // .substr(2).trim();
                    } else if (act.match('off')) {
                        newPayload["Rules"]["Rule"+k]["action_data"][i]["colour"] = 'off' 
                    }
                }   
            }
        }

        /* EVENT */
        // From the event label, the parameters of the tiles and their positions are extracted.
        let evt = newPayload["Rules"]["Rule"+k]["event_data"].label;
        //let reg = /tessera(?:(?!^\.)[\s\S])+/g;
        let numbers = [];
        if (evt.match(/placed/ig)) { //event is "Tile is placed"
            newPayload["Rules"]["Rule"+k]["event_data"]["evt"] = "placed"
        } else if (evt.match(/removed/ig)){ //event is "Tile is removed"
            newPayload["Rules"]["Rule"+k]["event_data"]["evt"] = "removed"
        } else if (evt.match(/arithmetic expression/ig)) {
            if (evt.match(/correct/ig)){
                newPayload["Rules"]["Rule"+k]["event_data"]["evt"] = "expr_correct"
            } else {
                newPayload["Rules"]["Rule"+k]["event_data"]["evt"] = "expr_wrong"
            }
        }
        /* STATES */
        // From the state label, the parameters of the tiles and their positions are extracted.
        for (i=0; i<state.length; i++) {
            let st = newPayload["Rules"]["Rule"+k]["state_data"][i].label;
            //let reg = /tessera(?:(?!^\.)[\s\S])+/g;
            if (st) {
                let reg = /Tile(?:(?!^\.)[\s\S])+/g;
                let numbers = [];
                let matches = st.match(reg)
                if (matches) {
                    numbers = matches.toString().split(" ");
                    newPayload["Rules"]["Rule"+k]["state_data"][i]["card"] = numbers[3];//numbers[1]
                    newPayload["Rules"]["Rule"+k]["state_data"][i]["position"] = numbers[11]; //numbers[3]
                }
            }
        }
    }
    msg.payload = newPayload
    return msg
} catch (e) {
    node.warn (e.stack);
    //return msg;
}
